import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { productsContext } from '../../../contexts/productContext';

const UpdateProduct = () => {
  const { oneProduct, getOneProduct, updateProduct, getCategories, categories } = useContext(productsContext);
  const { id } = useParams();

  const navigate = useNavigate();

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [category, setCategory] = useState("");
  const [image, setImage] = useState(null); 

  useEffect(() => {
    getOneProduct(id);
    getCategories();
  }, []);

  useEffect(() => {
    if(oneProduct){
      setTitle(oneProduct.title);
      setDescription(oneProduct.description);
      setPrice(oneProduct.price);
      setCategory(oneProduct.category.id);
    };
  }, [oneProduct]);

  function handleSaveChanges() {
    let updatedProduct = new FormData();
    updatedProduct.append("title", title);
    updatedProduct.append("description", description);
    updatedProduct.append("price", price);
    updatedProduct.append("category", category);
    if(image){
      updatedProduct.append("image", image);
    };
    updateProduct(id, updatedProduct, navigate);
  };

  return (
    <div>
      <h2>Update Product</h2>
      <input type="text" value={title} onChange={(e) => setTitle(e.target.value)}/>
      <input type="text" value={description} onChange={(e) => setDescription(e.target.value)}/>
      <input type="text" value={price} onChange={(e) => setPrice(e.target.value)}/> <br />

      <p>CATEGORY BEFORE: {oneProduct?.category.title}</p>
      <select value={category} onChange={(e) => setCategory(e.target.value)}>
        <option>Choose category</option>
        {categories?.map(item => (
          <option key={item.id} value={item.id}>{item.title}</option>
        ))}
      </select>

      <p>IMAGE BEFORE: {oneProduct ? oneProduct.image : 'Some file'}</p>
      <input accept="image/*" type="file" onChange={e => setImage(e.target.files[0])}/> <br />

      <button onClick={handleSaveChanges}>Save Changes</button>
    </div>
  )
}

export default UpdateProduct
